import React from 'react'
import gql from 'graphql-tag'
import { Query } from 'react-apollo'

//Query for a single post
const SINGLE_POST = gql `

query SinglePost {
  post(postId: 2){
    title
  }
}

` 

const Post = () => {
  return (
    <div>
      <h1>Post Component</h1>
    <div className='card'>
      
      <Query query={SINGLE_POST}>
        { ({ loading, error, data }) =>  {
          if(loading) return <h4>Loading</h4>
          if(error) console.log('Error')
          console.log(data)
          return <h2>test</h2>
        }}
      </Query>
    </div>
    </div>
  )
}

export default Post
