import React, { useState, useContext } from 'react'
import PageContext from '../context/PageContext'


const Input = () => {

  const { amountOfPosts } = useContext(PageContext)
  const [amount, setAmount] = useState(10)


  const handleChange = (e) => {
    setAmount(e.target.value)
    amountOfPosts(e.target.value)
  }

  return (
    <div className="form-group">
      <label for="exampleSelect1">Filter Number of Posts</label>
      <select value={amount} className="form-control" id="exampleSelect1" onChange={handleChange}>
        <option>10</option>
        <option>20</option>
        <option>30</option>
        <option>50</option>
      </select>
    </div>
  )
}

export default Input
